async function connect_wallet(){if(!window.ethereum)return void alert("MetaMask not detected. Please install MetaMask first.");const web3=new Web3(window.ethereum),message=["Sign this message to confirm you own this wallet address. This action will not cost any gas fees."].join("\n"),address=(await web3.eth.requestAccounts())[0],signature=await web3.eth.personal.sign(message,address);await $.ajax({type:"POST",headers:{accept:"application/json"},data:{message:message,address:address,signature:signature,_token:$('meta[name="csrf-token"]').attr("content")},url:"/meta-mask-login",success:function(response){window.location.href=response.url}})}